import styled from "styled-components";

export const CardContainer = styled.div`
  height: 216px;
  width: 384px;
  border-radius: 10px;
  margin-top: 15px;
`;

export const CardTop = styled.div`
  display: flex;
  flex-direction: row;
  height: 20px;
  width: 384px;
  background: #e1e1e1;
  align-items: center;
  border-radius: 5px 5px 0px 0px;
`;

export const CardDot = styled.div`
  height: 8px;
  width: 8px;
  border-radius: 4px;
  background: #ff7769;

  &:nth-child(1) {
    margin-left: 10px;
  }

  &:nth-child(2) {
    background: #ffc850;
    margin-left: 3px;
  }

  &:nth-child(3) {
    background: #00d369;
    margin-left: 3px;
  }
`;

export const CardBar = styled.div`
    height: 8px;
    width 320px;
    background: #fff;
    border-radius: 2px;
    margin-left: 10px;
`;
