import styled from "styled-components";

export const FooterContainer = styled.div`
  height: 40rem;
  width: 100%;
  display: flex;
  flex-direction: column;
  background: #f4d743;
`;

export const FooterLogo = styled.img`
  height: 100px;
  width: 100px;
`;

export const FooterH1 = styled.h1`
  margin-top: 1.5rem;
  color: #fff;
  font-size: 48px;
  text-align: center;
  @media screen and (max-width: 768px) {
    font-size: 40px;
  }
  @media screen and (max-width: 480px) {
    font-size: 32px;
  }
`;

export const FooterP = styled.p`
  color: #fff;
  font-size: 24px;
  text-align: center;
  @media screen and (max-width: 768px) {
    font-size: 24px;
  }
  @media screen and (max-width: 480px) {
    font-size: 18px;
  }
`;

export const FooterBtnRow = styled.div`
  display: flex;
  flex-direction: row;
  width: 100px;
  height 50px
`;

export const FooterBtn = styled.div`
  height: 50px;
  width: 50px;
  border-radius: 9px;
  background: #f4d743;
`;

export const FooterIcon = styled.div`
  height: 18px;
  width: 18px;
  border-radius: 9px;
  background: red;
`;
