import styled from "styled-components";

export const WorkContainer = styled.div`
  background: #fff;
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  z-index: 1;
`;

export const WorkBg = styled.div`
  width: 100%;
  height: 100%;
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
`;

export const WorkFrost = styled.div`
  clip-path: polygon(70% 0, 100% 0, 100% 100%, 30% 100%);
  position: absolute;
  right: 0;
  height: 100%;
  width: 100%;
  background: #f4d743;
  backdrop-filter: saturate(180%) blur(10px);
`;

export const AboutFrost2 = styled.div`
  clip-path: polygon(100% 0, 0 0, 100% 100%);
`;
