import React, { useState } from "react";
import {
  HeroContainer,
  HeroBg,
  HeroContent,
  HeroH1,
  HeroP,
  VideoBg,
} from "./HeroElms";
import Video from "../../videos/video.mp4";

const Hero = () => {
  const [hover, setHover] = useState(false);

  const onHover = () => {
    setHover(!hover);
  };

  return (
    <HeroContainer id="home">
      <HeroBg>
        <VideoBg autoPlay loop muted src={Video} type="video/mp4" />
      </HeroBg>
      <HeroContent>
        <HeroH1>Jacob Reppuhn</HeroH1>
        <HeroP>Artist / Developer / Designer</HeroP>
      </HeroContent>
    </HeroContainer>
  );
};

export default Hero;
