import styled from "styled-components";

export const CardContainer = styled.div`
  height: 216px;
  width: 384px;
  background: red;
  border-radius: 10px;
`;

export const CardTop = styled.div`
  display: flex;
  flex-direction: row;
  height: 20px;
  width: 384px;
  background: #111;
  align-items: center;
  justify-content: space-around;
`;

export const CardDot = styled.div`
  height: 8px;
  width: 8px;
  border-radius: 4px;
  background: red;

  &:nth-child(2) {
    background: yellow;
  }
`;

export const CardBar = styled.div`
    height: 8px;
    width 320px;
    background: #fff;
    border-radius: 2px;
`;

export const CardBg = styled.div`
  height: 108px;
  width: 250px;
  background: pink;
`;

export const CardBtn = styled.button`
  border: none;
`;
