import styled from "styled-components";

export const WorkContainer = styled.div`
  background: #fff;
  display: flex;
  height: 100vh;
  z-index: 1;
`;

export const WorkBg = styled.div`
  width: 100%;
  height: 100%;
  overflow: hidden;
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const WorkContent = styled.div`
  z-index: 3;
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
  background: rgba(255, 255, 255, 0.8);
  backdrop-filter: saturate(180%) blur(10px);
  height: 80%;
  width: 80%;
`;

export const WorkFrost = styled.div`
  clip-path: polygon(70% 0, 100% 0, 100% 100%, 30% 100%);
  position: absolute;
  right: 0;
  height: 100%;
  width: 100%;
  background: #979dac;
`;

export const AboutFrost2 = styled.div`
  clip-path: polygon(100% 0, 0 0, 100% 100%);
  position: absolute;
  right: 0;
  top: 100;
  height: 70%;
  width: 30%;
  background: #5fa8d3;
`;

export const CardColumn = styled.div`
  display: flex;
  flex-direction: column;
`;

export const WorkH1 = styled.h1`
  margin-top: 1.5rem;
  color: #5fa8d3;
  font-size: 48px;
  text-align: center;
  @media screen and (max-width: 768px) {
    font-size: 40px;
  }
  @media screen and (max-width: 480px) {
    font-size: 32px;
  }
`;
