import styled from "styled-components";

export const AboutContainer = styled.div`
  background: #fff;
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  z-index: 1;
`;

export const AboutBg = styled.div`
  width: 100%;
  height: 100%;
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
`;

export const AboutContent = styled.div`
  z-index: 3;
  position: absolute;
  display: flex;
  flex-direction: column;
  align-items: center;
  background: #181b22;
  height: 80%;
  width: 80%;
  border-radius: 25px;
  box-shadow: rgba(50, 50, 93, 0.25) 0px 6px 12px -2px,
    rgba(0, 0, 0, 0.3) 0px 3px 7px -3px;
`;

export const AboutFrost = styled.div`
  clip-path: polygon(30% 0, 100% 0, 100% 100%, 70% 100%);
  position: absolute;
  right: 0;
  height: 100%;
  width: 100%;
  background: #f4d743;
  backdrop-filter: saturate(180%) blur(10px);
`;

export const AboutH1 = styled.h1`
  margin-top: 1.5rem;
  color: #fff;
  font-size: 48px;
  text-align: center;
  @media screen and (max-width: 768px) {
    font-size: 40px;
  }
  @media screen and (max-width: 480px) {
    font-size: 32px;
  }
`;

export const AboutP = styled.p`
  color: #fff;
  font-size: 24px;
  text-align: center;
  @media screen and (max-width: 768px) {
    font-size: 24px;
  }
  @media screen and (max-width: 480px) {
    font-size: 18px;
  }
`;
