import styled from "styled-components";

export const WorkContainer = styled.div`
  background: #fff;
  display: flex;
  height: 100vh;
  z-index: 1;
`;

export const WorkBg = styled.div`
  width: 100%;
  height: 100%;
  overflow: hidden;
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const WorkContent = styled.div`
  z-index: 3;
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
  background: rgba(0, 0, 0, 0.8);
  backdrop-filter: saturate(180%) blur(10px);
  height: 80%;
  width: 80%;
  box-shadow: rgba(50, 50, 93, 0.25) 0px 30px 60px -12px,
    rgba(0, 0, 0, 0.3) 0px 18px 36px -18px;
`;

export const WorkFrost = styled.div`
  clip-path: polygon(70% 0, 100% 0, 100% 100%, 30% 100%);
  position: absolute;
  right: 0;
  height: 100%;
  width: 100%;
  background: #000;
`;

export const AboutFrost2 = styled.div`
  clip-path: polygon(100% 0, 0 0, 100% 100%);
  position: absolute;
  right: 0;
  top: 100;
  height: 70%;
  width: 30%;
  background: #f4d743;
`;

export const CardColumn = styled.div`
  display: flex;
  flex-direction: column;
`;

export const CardBg = styled.image`
  height: 196px;
  width: 384px;
  border-radius: 0px 0px 5px 5px;
  display: flex;
  background: #fff;
  justify-content: center;
`;

export const CardBtn = styled.button`
  border: none;
  border-radius: 5px;
  background: #00d369;
  padding: 5px;
  height: 2rem;
  margin-top: 155px;
  margin-left: 5px;
  margin-right: 5px;
`;
