import styled from "styled-components";

export const AboutContainer = styled.div`
  background: #fff;
  display: flex;
  justify-content: center;
  align-items: center;
  height: 150rem;
  z-index: 1;
`;

export const AboutBg = styled.div`
  width: 100%;
  height: 100%;
  overflow: hidden;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

export const AboutContent = styled.div`
  z-index: 3;
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
  background: rgba(255, 255, 255, 0.8);
  backdrop-filter: saturate(180%) blur(10px);
  height: 80%;
  width: 80%;
`;

export const AboutH1 = styled.h1`
  margin-top: 1.5rem;
  color: #fff;
  font-size: 48px;
  text-align: center;
  @media screen and (max-width: 768px) {
    font-size: 40px;
  }
  @media screen and (max-width: 480px) {
    font-size: 32px;
  }
`;

export const AboutP = styled.p`
  color: #fff;
  font-size: 24px;
  text-align: center;
  @media screen and (max-width: 768px) {
    font-size: 24px;
  }
  @media screen and (max-width: 480px) {
    font-size: 18px;
  }
`;

export const AboutIconRow = styled.div`
  display: flex;
  height: 100px;
`;

export const AboutIcon = styled.img`
  height: 5rem;
  width: 5rem;
  margin: 0 10px;

  &:first-of-type {
    margin-left: 0;
  }
  &:last-of-type {
    margin-right: 0;
  }
`;

export const AboutGreeting = styled.div`
  height: 38rem;
  width: 100%;
  background: #5fa8d3;
`;
