  align-items: center;
`;

export const AboutContent = styled.div`
  z-index: 3;
  position: absolute;
  margin-top: 25rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  background: #fff;
  border-radius: 0.8rem;
  height: 80%;
  width: 75%;
